def bubbleSort(array: List):
    length = len(array)
    for i in range(length, 0, -1):
        noSwap = True
        for j in range(i-1):
            if array[j] > array[j+1]:
                array[j], array[j+1] = array[j+1], array[j]
                noSwap = False
    if noSwap:
        return array
    return array

